import React, { useState } from 'react';
import { useParams } from "react-router-dom";
import Form from 'react-bootstrap/Form';
import axios from 'axios';

export default function Payment() {

  const {username, user_id } = useParams();
  const [formData, setFormData] = useState({
    amount: '',
    accountNumber: '',
  });
  const [bank,setBank]=useState("")

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    console.log(formData);
    try {
      const response = await axios.post('/api/payments', formData);
      console.log(response.data); // display payment confirmation message
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleFormSubmit}>
    <label>Amount:
        <input type="number" name="amount" value={formData.amount} onChange={handleFormChange}/>
    </label>
        <Form.Select style={{width:"0"}} aria-label="select bank"  value={bank ||"Select"}  onChange={e => {
            console.log(e.target.value);
            setBank(e.target.value);
          }} >
                    <option>Select Bank Name</option>
                    <option value="a" >Bank A</option>
                    <option value="b" >Bank B</option>
                    <option value="c" >Bank C</option>
        </Form.Select>
      <label> Account Number: 
        <input type="text" name="accountNumber" value={formData.accountNumber} onChange={handleFormChange}/>
      </label>
      <button type="submit">Pay Now</button>
    </form>
  );
}
