import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import Input from "./Input";
import SellerNav from '../Navbar/navbar';
import './product.css'

export default function ModifyProduct(){
    const [state, setState] = useState({
        name: "",
        discount: "",
        price:"",
        quantity:"",
        description:""
    });
    const navigate = useNavigate();
    const { user_id,id } = useParams();

   //  console.log('user_id',user_id)
   //  console.log('id',id)

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setState((prevProps) => ({
        ...prevProps,
        [name]: value
        }));
    };

    useEffect(() => {
        getProductById(id);
    }, []);
    
    const getProductById = async (id) => {
        const response = await axios.get(`http://localhost:5000/products/${id}`);
        setState(response.data)
        console.log(response.data)
    };
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
        await axios.patch(`http://localhost:5000/products/${id}`, state);
      //   navigate("/Product");
       navigate(`/Product/${user_id}`);
        } catch (error) {
        console.log(error);
        }
    };

    return(
      <div>
      <SellerNav/>
      <div class="product-body">
        <div class="product-container">
        <div class="product-text">CREATE PRODUCT</div>
        <form onSubmit={handleSubmit}>
           <div class="product-form-row">
              <Input name="name" inputName="Product Name" value={state.name} 
              onChange={handleInputChange}/>
              <Input name="discount" inputName="Discount" value={state.discount} 
              onChange={handleInputChange}/>
           </div>
           <div class="product-form-row">
              <Input name="price" inputName="Product Price" value={state.price} 
              onChange={handleInputChange}/>
              <Input name="quantity" inputName="Product Quantity" value={state.quantity} 
              onChange={handleInputChange}/>
           </div>
           <div class="product-form-row">
              <div class="input-data textarea">
                 <textarea rows="8" cols="80"name="description" value={state.description} 
                 onChange={handleInputChange} required></textarea>
                 <div class="underline"></div>
                 <label for="">Product Description</label>
              </div>
           </div>
         <div class="product-form-row product-submit-btn">
            <div class="input-data">
               <div class="inner"></div>
               <input type="submit" value="submit"/>
            </div>
         </div>
        </form>
     </div>
   </div>
   </div>
    );
}
