import React, { useState,useEffect } from 'react';
import axios from 'axios';
import { useNavigate,useParams } from "react-router-dom";
import './tracking.css'


export default function OrderTracking(props){

  const [iteminfo, setIteminfo] = useState([]);
  const {username, user_id,id } = useParams();

  const [state,setState]=useState([]);
    useEffect(() => {
        getItems();
    }, []);

    const getItems = async () => {
        const response = await axios.get(`http://localhost:5000/items_by_id/${id}`);
        setIteminfo(response.data);
        console.log(response.data)
    };

    return(
        <div className='order-track-body'>
          <div class="order-card">
            <div class="order-title">Purchase Reciept</div>
            <div class="order-pricing">
                <div class="row">
                    <div class="col-9">
                        <span id="name">Order No</span>  
                    </div>
                    <div class="col-3">
                        <span id="price">2345436547</span>
                    </div>
                    <div class="col-9">
                        <span id="name">Order Date</span>  
                    </div>
                    <div class="col-3">
                        <span id="price">12/2/2020</span>
                    </div>
                    <div class="col-9">
                        <span id="name">BEATS Solo 3 Wireless Headphones</span>  
                    </div>
                    <div class="col-3">
                        <span id="price">&pound;299.99</span>
                    </div>
                </div>
            </div>
            <div class="order-total">
                <div class="row">
                    <div class="col-9"></div>
                    <div class="col-3"><big>&pound;262.99</big></div>
                </div>
            </div>
            <div class="order-tracking">
                <div class="order-title">Tracking Order</div>
            </div>
            <div class="progress-track">
                <ul id="progressbar">
                    <li class="step0 active " id="step1">Ordered</li>
                    <li class="step0 active text-center" id="step2">Shipped</li>
                    <li class="step0 active  text-right" id="step3">On the way</li>
                    <li class="step0  text-right" id="step4">Delivered</li>
                </ul>
            </div>
            

            <div class="footer">
                <div class="row">
                    {/* <div class="col-2"><img class="img-fluid" src="https://i.imgur.com/YBWc55P.png"/></div> */}
                    <div class="col-10">Want any help? Please &nbsp;<a> contact us</a></div>
                </div>
                
               
            </div>
        </div>
         </div>

    );
}
