import { useState } from 'react';
import { Dropdown } from 'react-bootstrap';

function Payment() {
  const [selectedBank, setSelectedBank] = useState('');

  const handleBankChange = (event) => {
    setSelectedBank(event.target.value);
  };

  return (
    <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        {selectedBank || 'Select Bank Name'}
      </Dropdown.Toggle>
      <Dropdown.Menu>
        <Dropdown.Item onClick={handleBankChange} value="Bank A">
          Bank A
        </Dropdown.Item>
        <Dropdown.Item onClick={handleBankChange} value="Bank B">
          Bank B
        </Dropdown.Item>
        <Dropdown.Item onClick={handleBankChange} value="Bank C">
          Bank C
        </Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
}
