import './item.css'

export default function Item(props){

    const handleButtonAddCart = e => {
        e.preventDefault()
        props.addToCart(props.id,props.user_id,props.price)
    }

    const handleButtonBuyNow = e => {
        e.preventDefault()
        const data={
            item_id:id,
            customer_id:user_id
        }
        axios.post('http://localhost:5000/buy_item', data)
        .then(response => {
            console.log(response.data);
        })
        .catch(error => {
            console.error(error);
        });
    }

    return(
    <div class="product-card">
    <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/245657/t-shirt.png" alt=""/>
    <h2>{props.name}</h2>
    <p>{props.description}</p>
    <div class="item-btns">
        <button>Buy now</button>
        <button onClick={handleButtonAddCart} >Add to cart</button>
        <button onClick={handleButtonBuyNow} >Add to cart</button>
    </div>
    </div>
    );
}
