import React, { useState, useEffect } from "react";
import SellerNav from '../Navbar/navbar';
import { useParams } from "react-router-dom";
import axios from "axios";
import './dashboard.css'

export default function Dashboard(){

    const { username,user_id } = useParams();
    const [total_items,setTotal_items]=useState([])

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        const response = await axios.get(`http://localhost:5000/items_by_seller/${user_id}`);
        console.log(response.data.length)
        setTotal_items(response.data.length);
    };


      return (
   <div className='dashboard-body'>
    <SellerNav/>
    <div className='dashboard-total-amount'>
        <h3>Total Amount</h3>
        <p className='d-amount'>$150000</p>
    </div>
    <div className='dashboard-items'>
        <div className='dashboard-box total-items'>
            <div><p className='d-titles'><b>Total Items</b></p></div>
            <div><p className='d-titles'>{total_items}</p></div>
        </div>
        <div className='dashboard-box items-sold'>
            <div><p className='d-titles'><b>Items Sold</b></p></div>
            <div><p className='d-titles'>10</p></div>
        </div>
        <div className='dashboard-box total-orders'>
            <div><p className='d-titles'><b>Orders</b></p></div>
            <div><p className='d-titles'>10</p></div>
        </div>
        <div className='dashboard-box total-returns'>
            <div><p className='d-titles'><b>Returned</b></p></div>
            <div><p className='d-titles'>10</p></div>
        </div>
    </div>
   </div>
  );
}
