import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link,useParams } from "react-router-dom";
import ProductCard from './productCard';
// import SellerNav from '../Navbar/navbar';
import './productList.css'

export default function ProductList(props){

    const [products, setProduct] = useState([]);
    const { user_id } = useParams();

    // console.log(user_id)

    useEffect(() => {
        getProducts(user_id);
    }, []);
    
    const getProducts = async (user_id) => {
        const response = await axios.get(`http://localhost:5000/seller_products/${user_id}`);
        setProduct(response.data);
    };
    
    const deleteProduct = async (id) => {
        try {
        console.log('delete',id)
        await axios.delete(`http://localhost:5000/products/${id}`);
        getProducts(user_id);
        } catch (error) {
        console.log(error);
        }
    };

    return(
    <div>
        <SellerNav/>
        <div className='product-list-body'>
            <div className='product-list-add'>
                <Link  to={`/${user_id}/createProduct`}>
                <button className='product-list-button'>Add new item </button> 
                </Link>
            </div>
            {products.map((product, index) => (
            <ProductCard 
            key={product._id} 
            name={product.name} 
            description={product.description}
            quantity={product.quantity}
            price={product.price}
            discount={product.discount}
            deleteProduct={() => deleteProduct(product._id)}
            Linkto={`edit/${product._id}`}
             />
            ))}
        </div>       
    </div>
    );
}
