        getItems();
    }, []);

    
    const getItems = async () => {
        const response = await axios.get('http://localhost:5000/all_items');
        setItemsinfo(response.data);
        console.log(response.data)
    };


    const addToCart=  async (item_id,user_id,price)=>{
        try {
          const data={
              user_id:user_id,
              item:item_id,
              quantity:1,
              bill:price
            }
            state.cart.push(data)
            console.log(data)
            console.log(state)
        await axios.patch(`http://localhost:5000/customers/${user_id}`, state);
        } catch (error) {
        console.log(error);
        }
    }
    
  return (
   <div>
    <Header/>
    <Search/>
    <div className='items'>
      <div className='items-container'>
        {itemsinfo.map((item) => (
            <Item id={item._id} name={item.name} description={item.description} 
              addToCart={addToCart} user_id={user_id} price={item.price} />
        ))}
      </div>
    </div>

   </div>
  );
}
