import React from "react";
import axios from 'axios';
import { useNavigate,useParams } from "react-router-dom";
import './item.css'

export default function Item(props){

    const navigate=useNavigate();
    const {username, user_id } = useParams();
    const handleButtonAddCart = e => {
        e.preventDefault();
        axios.post(`http://localhost:5000/post_cart/${user_id}`,props)
        .then((response) => {
        console.log(`cart with id ${response._id}.`);
        }).catch((err) => {
            console.error(err);
        });
    }

    const item_id=props.id
    const handleButtonBuyNow = e => {
        e.preventDefault();
        navigate(`/${username}/${user_id}/${item_id}/Payment`)
    }

    return(
    <div class="product-card">
    <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/245657/t-shirt.png" alt=""/>
    <h2>{props.name}</h2>
    <p>{props.description}</p>
    <p><b>Price</b>{" $" +props.price}</p>
    <div class="item-btns">
        <button onClick={handleButtonBuyNow}>Buy now</button>
        <button onClick={handleButtonAddCart} >Add to cart</button>
    </div>
    </div>
    );
}
