import React from "react";
import axios from 'axios';
import {  json, Link,useNavigate } from 'react-router-dom'
import { useState, useEffect } from "react";
import './payment.css'

export default function Payment(props){

    const [state, setState] = useState({
        email: "",
        password: "",
        usertype:"Seller"
    });
    const navigate=useNavigate();


    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setState((prevProps) => ({
        ...prevProps,
        [name]: value
        }));
    };

    useEffect(() => {
        handleSubmit();
    }, []);
    
    const handleSubmit =async (e) => {
        e.preventDefault();
    };


    return(
      <div className="login-body">
      <div class="wrapper">
         <div class="title-text">
            <div class="title login">Payment</div>
         </div>
         <div class="form-container">
            <div class="form-inner">
               <form onSubmit={handleSubmit} class="login">
                 <div class="pay" >
                    Amount to Pay<span className="payamount"> $300</span> 
                  </div>
                  <div class="field ">
                     <input name="email" type={"text"} placeholder="Account Number" value={state.user} onChange={handleInputChange}  required/>
                  </div>
                  <div class="field">
                     <input name="password"  type={"password"} placeholder="Password" value={state.password} onChange={handleInputChange}  required/>
                  </div>
                  <div class="field btn-login">
                     <div class="btn-layer"></div>
                     <input type={"submit"} value="Pay Now"/>
                  </div>
               </form>

            </div>
         </div>
      </div>
   </div>
    );
}
