import React, { useState } from 'react';
import { useParams } from "react-router-dom";
import Dropdown from 'react-bootstrap/Dropdown';
import axios from 'axios';

export default function Payment() {

  const {username, user_id } = useParams();
  const [formData, setFormData] = useState({
    amount: '',
    accountNumber: '',
  });
  const [bank,setBank]=useState("")

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    console.log(formData);
    try {
      const response = await axios.post('/api/payments', formData);
      console.log(response.data); // display payment confirmation message
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleFormSubmit}>
    <label>Amount:
        <input type="number" name="amount" value={formData.amount} onChange={handleFormChange}/>
    </label>
    <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
            {bank || 'Select Bank Name'}
        </Dropdown.Toggle>
        <Dropdown.Menu>
            <Dropdown.Item value="Bank A" onChange={e => {
            console.log(e.target.value);
            setBank(e.target.value);
          }} >
            Bank A
            </Dropdown.Item>
            <Dropdown.Item onClick={handleFormChange} value="Bank B">
            Bank B
            </Dropdown.Item>
            <Dropdown.Item onClick={handleFormChange} value="Bank C">
            Bank C
            </Dropdown.Item>
        </Dropdown.Menu>
        </Dropdown>
      <label> Account Number: 
        <input type="text" name="accountNumber" value={formData.accountNumber} onChange={handleFormChange}/>
      </label>
      <button type="submit">Pay Now</button>
    </form>
  );
}
