import React, { useState } from "react";
import axios from "axios";
import { Link,useParams,useNavigate } from "react-router-dom";
import Input from "./Input";
import SellerNav from '../Navbar/navbar';
import './product.css'

export default function Item(){
    const [state, setState] = useState({
        item_name: "",
        price:"",
        available_qty:"",
        item_desc:"",
    });
    const { user_id } = useParams();
   const navigate = useNavigate();

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setState((prevProps) => ({
        ...prevProps,
        [name]: value
        }));
    };

   const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(state)
   axios.post("http://localhost:5000/post_items", state)
      .then(response => {
         console.log(response.data);
            navigate('/Login')
         })
      .catch(error => {
         console.error(error);
      });
  };

    return(
      <div>
      <SellerNav/>
      <div class="product-body">
        <div class="product-container">
        <div class="product-text">CREATE PRODUCT</div>
        <form onSubmit={handleSubmit}>
           <div class="product-form-row">
              <Input name="item_name" inputName="Product Name" value={state.item_name} 
              onChange={handleInputChange}/>
              <Input name="discount" inputName="Discount" value={state.discount} 
              onChange={handleInputChange}/>
           </div>
           <div class="product-form-row">
              <Input name="price" inputName="Product Price" value={state.price} 
              onChange={handleInputChange}/>
              <Input name="available_qty" inputName="Product Quantity" value={state.available_qty} 
              onChange={handleInputChange}/>
           </div>
           <div class="product-form-row">
              <div class="input-data textarea">
                 <textarea rows="8" cols="80"name="item_desc" value={state.item_desc} 
                 onChange={handleInputChange} required></textarea>
                 <div class="underline"></div>
                 <label for="">Product Description</label>
              </div>
           </div>
         <div class="product-form-row product-submit-btn">
            <div class="input-data">
               <div class="inner"></div>
               <input type="submit" value="submit"/>
            </div>
         </div>
         {/* </Link> */}
        </form>
     </div>
   </div>
   </div>
    );
}


