import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const CheckoutForm = () => {
  const [paymentError, setPaymentError] = useState(null);
  const [paymentSuccess, setPaymentSuccess] = useState(null);
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      return;
    }

    // Get payment details from the form
    const cardElement = elements.getElement(CardElement);
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: 'card',
      card: cardElement,
    });

    if (error) {
      setPaymentError(error.message);
      setPaymentSuccess(null);
    } else {
      // Send paymentMethod.id to your server to process the payment
      const response = await fetch('/api/payment', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          paymentMethodId: paymentMethod.id,
        }),
      });

      const data = await response.json();

      if (data.success) {
        setPaymentSuccess('Payment successful!');
        setPaymentError(null);
      } else {
        setPaymentError(data.error);
        setPaymentSuccess(null);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="card-element">
          Credit or debit card
        </label>
        <CardElement className="form-control" id="card-element" />
      </div>

      {paymentError && <div className="alert alert-danger">{paymentError}</div>}
      {paymentSuccess && <div className="alert alert-success">{paymentSuccess}</div>}

      <button type="submit" className="btn btn-primary">
        Pay
      </button>
    </form>
  );
};

export default CheckoutForm;
