import React from "react";
import axios from 'axios';
import { useNavigate,useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import Form from 'react-bootstrap/Form';
import './CouponForm.css'

export default function CouponForm(props){
    const [order,setOrder]=useState([])
    const [formData, setFormData] = useState({
        amount: '',
        bank_name:'',
        account_number: '',
    });
    const handleFormChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
        }));
    };

    const navigate=useNavigate();

    const {username, user_id,} = useParams();

    useEffect(() => {
        getOrder();
        handleSubmit();
    }, []);

    const getOrder = async () => {
        const response = await axios.get(`http://localhost:5000/createCoupon`);
        setOrder(response.data);
        console.log(response.data)
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
       const data={
         ...formData,
         amount:order.bill,
         customer_id:user_id
        }
        console.log(data);
        try {
          console.log('payments')
        const response = await axios.post('http://localhost:5000/payments', data);
        console.log(response.data); // display payment confirmation message
        if (response.status==200){
          navigate(`/${username}/${user_id}/AdvertiserHome`)
        }
        } catch (error) {
        console.error(error);
        }
    };
    return(
      <div className="login-body">
      <div class="wrapper">
         <div class="title-text">
            <div class="title login">Create Coupon</div>
         </div>
         <div class="form-container">
            <div class="form-inner">
               <form onSubmit={handleSubmit} class="login">
                  <div class="field ">
                    <input type="text" name="bank_name"  placeholder="Coupon Name" value={formData.bank_name} onChange={handleFormChange}/>
                  </div>
                  <div class="field ">
                    <input type="text" name="account_number"  placeholder="Discount" value={formData.account_number} onChange={handleFormChange}/>
                  </div>
                  {/* <div class="pay" >
                   Expiry Date
                  </div> */}
                   <div style={{marginTop:"9px",marginBottom:"9px",fontSize:"18px"}}>
                    <b>Validity Date</b>
                  </div>
                  <div class="field" style={{marginTop:"0px"}}>
                    <input type="date" name="account_number"  placeholder="Account Number" value={formData.account_number} onChange={handleFormChange}/>
                  </div>
                  <div class="field btn-login">
                     <div class="btn-layer"></div>
                     <input type={"submit"} value="Add Coupon"/>
                  </div>
               </form>

            </div>
         </div>
      </div>
   </div>
    );
}
