import React from "react";
import axios from 'axios';
import {  json, Link,useNavigate } from 'react-router-dom'
import { useState, useEffect } from "react";
import './login.css'

export default function Login(props){

    const [state, setState] = useState({
        email: "",
        password: "",
        usertype:"seller"
    });
    const navigate=useNavigate();

    const [user,setUser]=useState(null);
    
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setState((prevProps) => ({
        ...prevProps,
        [name]: value
        }));
    };

    useEffect(() => {
        handleSubmit();
    }, []);
    
    const handleSubmit =async (e) => {
        e.preventDefault();
        console.log(state);
         const data={
            email:state.email,
            password:state.password
         }
        if(state.usertype==="seller"){
         try {
               console.log(data);
               const link="http://localhost:5000/verify_"+state.usertype
               axios.post(link, data)
                .then(response => {
                    console.log(response.data);
                    if (response.data.success){
                        const user={
                           username:response.data.user.username,
                           user_id:response.data.user.id
                        }
                        setUser(response.data.user.username)
                        console.log(user)
                        navigate(`/${user_id}/Home`);
                    }

      
                })
                .catch(error => {
                    console.error(error);
                    navigate('/Login')
            });
               
         } catch (error) {
            console.log(error);
         }

        }if(state.usertype==="customer"){
            try {
                  console.log(state)
                  const data={
                  email:state.email,
                  password:state.password
                  }
                  console.log(data)
                  const response=await axios.post("http://localhost:5000/verifyCustomer", data);
                  console.log(response)
                  if(response.data.success){
                     console.log("yes ,found customer in database")
                     console.log(response.data)
                     const user_id=response.data.customer._id
                     const a={
                        user_id:response.data.customer._id,
                        user_name:response.data.customer.name
                     }
                     console.log(a)
                     setUser(a)
                     // console.log(user)
                     navigate(`/Home/${user_id}`);
                  }
                  
            } catch (error) {
               console.log(error);
            }

        }if(state.usertype==="advertiser"){
            try {
                  // console.log(state)
                  const data={
                  email:state.email,
                  password:state.password
                  }
                  console.log(data)
                  const response=await axios.post("http://localhost:5000/verifyAdvertiser", data);
                  console.log(response)
                  if(response.data.success){
                     console.log("yes ,found advertiser in database")
                     console.log(response.data)
                     const user_id=response.data.advertiser._id
                     const a={
                        user_id:response.data.advertiser._id,
                        user_name:response.data.advertiser.name
                     }
                     console.log(a)
                     setUser(a)
                     // console.log(user)
                     navigate(`/AdvertiserHome/${user_id}`);
                  }
                  
            } catch (error) {
               console.log(error);
            }
        }
    };


    return(
      <div className="login-body">
      <div class="wrapper">
         <div class="title-text">
            <div class="title login">Login</div>
         </div>
         <div class="form-container">
            <div class="form-inner">
               <form onSubmit={handleSubmit} class="login">
                  <div class="radio-check-buttons">
                     <div class="radio-check">
                        <input class="radio-check-input" type="radio"  value="seller" onChange={handleInputChange} name="usertype"  id="seller1" checked={state.usertype=="seller"}  />
                        <label class="radio-check-label" for="seller1">
                        Seller
                        </label>
                     </div>
                     <div class="radio-check">
                        <input class="radio-check-input" type="radio" value="customer" onChange={handleInputChange} name="usertype" id="customer1" checked={state.usertype=="customer"}/>
                        <label class="radio-check-label" for="customer1">
                        Customer
                        </label>
                     </div>
                        <div class="radio-check">
                        <input class="radio-check-input" type="radio" value="advertiser" onChange={handleInputChange} name="usertype" id="advertiser1" checked={state.usertype=="advertiser"}/>
                        <label class="radio-check-label" for="advertiser1">
                        Advertiser
                        </label>
                     </div>
                  </div>
                  <div class="field">
                     <input name="email" type={"email"} placeholder="Email Address" value={state.user} onChange={handleInputChange}  required/>
                  </div>
                  <div class="field">
                     <input name="password"  type={"password"} placeholder="Password" value={state.password} onChange={handleInputChange}  required/>
                  </div>
                  <div class="pass-link">
                     <a href="#">Forgot password?</a>
                  </div>
                  {/* <Link to={"/Product"}> */}
                  <div class="field btn-login">
                     <div class="btn-layer"></div>
                     <input type={"submit"} value="Login"/>
                  </div>
                  {/* </Link> */}
                  <div class="signup-link">
                     Not a member? <Link to={"/"}>Signup now</Link>
                  </div>
               </form>

            </div>
         </div>
      </div>
   </div>
    );
}
