    };
    
    

    return(
        <div className='order-track-body'>
          <div class="order-card">
            <div class="order-title">Order Reciept</div>
            <div class="order-pricing">
                <div class="row">
                    <div class="col-9">
                        <span id="name">Order No</span>  
                    </div>
                    <div class="col-3">
                        <span id="price">{"1234567"}</span>
                    </div>
                    <div class="col-9">
                        <span id="name">Order Date</span>  
                    </div>
                    <div class="col-3">
                        <span id="price">12/2/2020</span>
                    </div>
                    <div class="col-9">
                        <span id="name">Item name</span>  
                    </div>
                    <div class="col-3">
                        <span id="price">{iteminfo.item_name}</span>
                    </div>
                </div>
            </div>
            <div class="order-total">
                <div class="row">
                    <div class="col-9"></div>
                    <div class="col-3"><big>&pound;{iteminfo.price}</big></div>
                </div>
            </div>
            <div class="order-tracking">
                <div class="order-title">Tracking Order</div>
            </div>
            <div class="progress-track">
                <ul id="progressbar">
                    <li class="step0 active " id="step1">Ordered</li>
                    <li class="step0 active text-center" id="step2">Shipped</li>
                    <li class="step0 active  text-right" id="step3">On the way</li>
                    <li class="step0  text-right" id="step4">Delivered</li>
                </ul>
            </div>
        </div>
         </div>

    );
}
