import React, { useState,useEffect } from 'react';
import { useParams } from "react-router-dom";
import axios from 'axios';

function Search() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

 
  const handleSearch = (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    // Perform search logic here
    const results = performSearch(query);
    setSearchResults(results);
  }

  const performSearch = (query) => {
    // Perform search logic here and return results
    // For example, search an array of items for matching names
    const items = ['Apple', 'Banana', 'Orange', 'Pear'];
    const filteredItems = items.filter(item => item.toLowerCase().includes(query.toLowerCase()));
    return filteredItems;
  }

  return (
    <div>
      {/* <input type="text" value={searchQuery} onChange={handleSearch} placeholder="Search" />
      <ul>
        {searchResults.map(result => <li key={result}>{result}</li>)}
      </ul> */}
    </div>
  );
}

export default Search;
