import React from "react";
import axios from 'axios';
import {  Link,useNavigate } from 'react-router-dom'
import { useState, useEffect } from "react";
import Form from 'react-bootstrap/Form';
import './signUp.css'

export default function Register(props){

    const [formData, setFormData] = useState({
        name:'',
        username: '',
        password: '',
        email: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const data={
            name:formData.name,
            username:formData.username,
            password:formData.password,
            email:formData.email
        }
        if(formData.password===formData.confirm_password){
            // if(type=="seller"){
            //     axios.post('http://localhost:5000/sellers', data)
            //     .then(response => {
            //         console.log(response.data);
            //         setFormData({
            //         name:'',
            //         username: '',
            //         password: '',
            //         email: '',
            //         confirm_password:''
            //         });
            //     })
            //     .catch(error => {
            //         console.error(error);
            //     });
            // }
            // if(type=="customer"){
            //     axios.post('http://localhost:5000/customers', data)
            //     .then(response => {
            //         console.log(response.data);
            //         setFormData({
            //         name:'',
            //         username: '',
            //         password: '',
            //         email: '',
            //         confirm_password:''
            //         });
            //     })
            //     .catch(error => {
            //         console.error(error);
            //     });
            // }
            // if(type=="advertiser"){
            //     axios.post('http://localhost:5000/advertisers', data)
            //     .then(response => {
            //         console.log(response.data);
            //         setFormData({
            //         name:'',
            //         username: '',
            //         password: '',
            //         email: '',
            //         confirm_password:''
            //         });
            //     })
            //     .catch(error => {
            //         console.error(error);
            //     });
            // }
            axios.post('http://localhost:5000/'+type, data)
                .then(response => {
                    console.log(response.data);
                    setFormData({
                    name:'',
                    username: '',
                    password: '',
                    email: '',
                    confirm_password:''
                    });
                })
                .catch(error => {
                    console.error(error);
            });
        }else{
            console.log("password not matched!!!")
        }
    };

    const navigate=useNavigate();
    const [type,setType]=useState("");

    return(
        <div className="register-body">
        <div class="wrapper">
         <div class="title-text">
            <div class="title signup">Signup</div>
         </div>
         <div class="form-container">
            <div class="form-inner">
               <form onSubmit={handleSubmit} class="signup">
                <div class="field">
                    <Form.Select aria-label="select user" value={type}  onChange={e => {
            console.log(e.target.value);
            setType(e.target.value);
          }} >
                    <option>User Type</option>
                    <option value="customer" >Customer</option>
                    <option value="seller" >Seller</option>
                    <option value="advertiser" >Advertiser</option>
                    </Form.Select>
                </div>
                  <div class="field">
                     <input type="text" placeholder="Name" name="name" value={formData.name} 
              onChange={handleChange} required/>
                  </div>
                <div class="field">
                     <input type="text" placeholder="Username" name="username" value={formData.username}
              onChange={handleChange} required/>
                  </div>
                  <div class="field">
                     <input type="email" placeholder="Email Address" name="email" value={formData.email} 
              onChange={handleChange} required/>
                  </div>
                  <div class="field">
                     <input type="password" placeholder="Password" name="password" value={formData.password} 
              onChange={handleChange} required/>
                  </div>
                  <div class="field">
                     <input type="password" placeholder="Confirm password" name="confirm_password" value={formData.confirm_password} 
              onChange={handleChange} required/>
                  </div>
                  <div class="field btn-login">
                     <div class="btn-layer"></div>
                     <input type={"submit"} value="Signup"/>
                  </div>
                <div class="signup-link">
                     Already have an account? <Link to={"/Login"}>Login</Link>
                  </div>
               </form>
            </div>
         </div>
      </div>
    </div>
        // )}
        // </div>
      
    );
}
