import React from "react";
import axios from 'axios';
import {  json, Link,useNavigate } from 'react-router-dom'
import { useState, useEffect } from "react";
import Form from 'react-bootstrap/Form';
import './payment.css'

export default function Payment(props){
    const [bank,setBank]=useState("")
    const [formData, setFormData] = useState({
        amount: 200,
        accountNumber: '',
    });
    const handleFormChange = (event) => {
        const { name, value } = event.target;
        setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value,
        }));
    };

    const navigate=useNavigate();

    useEffect(() => {
        handleSubmit();
    }, []);
    
    const handleSubmit = async (event) => {
        event.preventDefault();
   
       const data={
         ...formData,
         bankName:bank
        }
        console.log(data);
        try {
        const response = await axios.post('/api/payments', formData);
        console.log(response.data); // display payment confirmation message
        } catch (error) {
        console.error(error);
        }
    };

    return(
      <div className="login-body">
      <div class="wrapper">
         <div class="title-text">
            <div class="title login">Payment</div>
         </div>
         <div class="form-container">
            <div class="form-inner">
               <form onSubmit={handleSubmit} class="login">
                 <div class="pay" >
                    Amount to Pay<span className="pay-amount"> $300</span> 
                  </div>
                   <Form.Select style={{height:"50px", marginTop:"10px"}} aria-label="select bank"  value={bank ||"Select"}  onChange={e => {
                    console.log(e.target.value);
                    setBank(e.target.value);
                }} >
                            <option>Select Bank Name</option>
                            <option value="Bank A" >Bank A</option>
                            <option value="Bank B" >Bank B</option>
                            <option value="Bank C" >Bank C</option>
                </Form.Select>
                  <div class="field ">
                    <input type="text" name="accountNumber"  placeholder="Account Number" value={formData.accountNumber} onChange={handleFormChange}/>
                  </div>
                  <div class="field btn-login">
                     <div class="btn-layer"></div>
                     <input type={"submit"} value="Pay Now"/>
                  </div>
               </form>

            </div>
         </div>
      </div>
   </div>
    );
}
