import React, { useState } from 'react';
import { useParams } from "react-router-dom";
import axios from 'axios';

export default function Payment() {

  const {username, user_id } = useParams();
  const [formData, setFormData] = useState({
    amount: '',
    paymentMethod: '',
    accountNumber: '',
  });

  const handleFormChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    console.log(formData);
    try {
      const response = await axios.post('/api/payments', formData);
      console.log(response.data); // display payment confirmation message
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <label>
        Amount:
        <input type="number" name="amount" value={formData.amount} onChange={handleFormChange}/>
      </label>
      <label>
        Bank Name:
        <select name="paymentMethod" def value={formData.paymentMethod} onChange={handleFormChange}>
          <option value="credit card">Bank A</option>
          <option value="debit card">Bank B</option>
          <option value="paypal">Bank C</option>
        </select>
      </label>
      <label> Account Number: 
        <input type="text" name="accountNumber" value={formData.accountNumber} onChange={handleFormChange}/>
      </label>
      <button type="submit">Pay Now</button>
    </form>
  );
}
